def run_query(query):
    #print (query)
    r = requests.post('https://hackerone.com/graphql', json=query, headers=headers)
    print (len(r.json()['data']['hacktivity_items']['edges']))
    for i in range(100):
        result_node= r.json()['data']['hacktivity_items']['edges'][i]['node']
        print (result_node['report']['url'], result_node['report']['title'], result_node['severity_rating'], result_node['total_awarded_amount'])
    
